

window.addEventListener('DOMContentLoaded', () => {

  const servicesLinks = document.querySelectorAll('.raspilMenu__item'),
        servicesContent = document.querySelectorAll('.raspil__services'),
        tabs = document.querySelectorAll('.tabs__item'),
        tabsContent = document.querySelectorAll('.product')

//--------------------------------------------------

  const hideContent = (contents, links, c) => {
    contents.forEach(item => {
      item.classList.remove(c, 'fade')
      item.classList.add('hide')
    })
    links.forEach(item => item.classList.remove('tabs__item-active'))
  }

  const showContent = (contents, links, c, id = 0) => {
    contents[id].classList.remove('hide')
    contents[id].classList.add(c, 'fade')

    links[id].classList.add('tabs__item-active')
  }

//--------------------------------------------------

servicesLinks.forEach((item, index) => {
  item.addEventListener('click', () => {
    hideContent(servicesContent, servicesLinks, 'show')
    showContent(servicesContent, servicesLinks, 'show', index)
  })
})

  tabs.forEach((item, index) => item.addEventListener('click', () => {
    hideContent(tabsContent, tabs, 'showFlex')
    showContent(tabsContent, tabs, 'showFlex', index)
  }))

//--------------------------------------------------

  hideContent(tabsContent, tabs, 'showFlex')
  showContent(tabsContent, tabs, 'showFlex', 0)

  hideContent(servicesContent, servicesLinks, 'show')
  showContent(servicesContent, servicesLinks, 'show', 0) 
})

